START TRANSACTION;

USE BUDGET; 

DROP TABLE IF EXISTS PROJECT;
CREATE TABLE PROJECT (
	IDX INT NOT NULL, 
	DATE date, 
	NAME text, 
	CREATOR text,
    PRIMARY KEY (IDX));

DROP TABLE IF EXISTS PROJECT_DATA;
CREATE TABLE PROJECT_DATA (
    IDX INT NOT NULL,
    DATE DATE,
    PROJECT_NAME TEXT,
    CLASS TEXT,
    TYPE TEXT,
    NAME TEXT,
    VALUE REAL,
    SENDER TEXT,
    RECIPIENT TEXT,
    CREATOR TEXT,
    PRIMARY KEY (IDX)
);

DROP TABLE IF EXISTS TRANSACTION_CLASSES;
CREATE TABLE TRANSACTION_CLASSES(
	IDX INT NOT NULL,
	CLASS text,
    PRIMARY KEY (IDX));

DROP TABLE IF EXISTS TRANSACTION_RECIPIENT;
CREATE TABLE TRANSACTION_RECIPIENT (
	IDX INT NOT NULL,
	RECIPIENT text,
    PRIMARY KEY (IDX));

DROP TABLE IF EXISTS TRANSACTION_SENDER;
CREATE TABLE TRANSACTION_SENDER (
	IDX INT NOT NULL,
	SENDER text,
    PRIMARY KEY (IDX));

DROP TABLE IF EXISTS TRANSACTION_TYPES;
CREATE TABLE TRANSACTION_TYPES (
	IDX INT NOT NULL,
	TYPE text,
    PRIMARY KEY (IDX));

